import React, { useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  useNavigate,
} from "react-router-dom";
import { jwtDecode } from "jwt-decode";
import axios from "axios";
import CircularProgress from "@mui/material/CircularProgress";

import Navbar from "./components/Navbar";
import Home from "./components/Home";
import Login from "./components/Login";
import Admin from "./components/Admin";
import Dashboard from "./components/Dashboard";

const App = () => {
  // Define state to store token, admin list, and loading state
  const [decodedToken, setDecodedToken] = useState(false);
  const [adminUsers, setAdminUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate(); // Get navigate function

  const checkAuth = () => {
    const storedToken = localStorage.getItem("token");
    if (storedToken) {
      try {
        const decodedToken = jwtDecode(storedToken);
        setDecodedToken(decodedToken);
      } catch (error) {
        console.error("Error decoding token:", error);
        setDecodedToken(false);
      }
    } else {
      setDecodedToken(false);
    }
  };

  const fetchAdminUsers = async () => {
    try {
      const response = await axios.get("/api/get_admin");
      setAdminUsers(response.data.admins);
      setLoading(false);
    } catch (error) {
      console.error("Error fetching admin users:", error);
      setLoading(false);
    }
  };

  useEffect(() => {
    checkAuth();
    fetchAdminUsers();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token"); // Clear token from local storage
    setDecodedToken(false); // Set decodedToken to false
    navigate("/login"); // Use navigate function to redirect to login page after logout
  };

  if (loading) {
    return (
      <div
        style={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          height: "100vh",
        }}
      >
        <CircularProgress />
      </div>
    );
  }

  return (
    <div className="App">
      <Router>
        <Navbar handleLogout={handleLogout} />
        <Routes>
          <Route
            path="/"
            element={<Home setDecodedToken={setDecodedToken} />}
          />
          <Route
            path="/login"
            element={<Login setDecodedToken={setDecodedToken} />}
          />

          {decodedToken ? (
            <Route path="/dashboard" element={<Dashboard />} />
          ) : (
            <Route
              path="/dashboard"
              element={<Login setDecodedToken={setDecodedToken} />}
            />
          )}

          {decodedToken && adminUsers.includes(decodedToken.email) ? (
            <Route path="/admin" element={<Admin />} />
          ) : (
            <Route
              path="/admin"
              element={<Login setDecodedToken={setDecodedToken} />}
            />
          )}
        </Routes>
      </Router>
    </div>
  );
};

export default App;
