import React, { useState, useEffect } from 'react';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import { Line } from 'react-chartjs-2';
import { Container, Typography, Grid, Paper, Select, MenuItem, CircularProgress } from '@mui/material';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const options = {
  responsive: true,
  plugins: {
    legend: {
      position: 'top',
    },
    title: {
      display: true,
      text: 'Chart.js Line Chart',
    },
  },
};

const Dashboard = () => {
  const [loading, setLoading] = useState(true);
  const [billData, setBillData] = useState([]);
  const [quarterMetricData, setQuarterMetricData] = useState([]);
  const [monthMetricData, setMonthMetricData] = useState([]);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await fetch('/api/get_data');
      const data = await response.json();

      setBillData(data.bill_data);
      setQuarterMetricData(data.quarter_metric_data);
      setMonthMetricData(data.month_metric_data);
      setLoading(false);
      console.log('Data successfully fetched: ', data)
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  // Dyamic data for graphs
  const lineChartData = {
    labels: monthMetricData.map(item => item.Month), // x-axis labels
    datasets: [
      {
        label: 'Total Charge', // dataset label
        data: monthMetricData.map(item => item.Total_Charge), // y-axis data
        borderColor: 'rgb(255, 99, 132)', // border color
        backgroundColor: 'rgba(255, 99, 132, 0.5)', // background color
      },
    ],
  };

  const barChartData = {
    // ...
  };

  const doughnutChartData = {
    // ...
  };

  return (
    <Container>
      <Typography variant="h3" gutterBottom>Data Dashboard</Typography>
      {loading ? (
        <CircularProgress />
      ) : (
        <Grid container spacing={3}>
          {/* Graph section 1 */}
          {/* Graph section 2 */}
          {/* Graph section 3 */}
          {/* Metric Section 1 */}
          {/* Metric Section 2 */}
        </Grid>
      )}
    </Container>
  );
};

export default Dashboard;
