import React, { useState } from "react";
import axios from "axios";
import { Typography, Container, Button, Alert } from "@mui/material";

const Admin = () => {
  const [uploadedFile, setUploadedFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [uploadError, setUploadError] = useState(null);

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    setUploadedFile(file);
    console.log("Uploaded file:", file);
  };

  const handleDataUpload = () => {
    if (!uploadedFile) {
      setUploadError("Please upload a file before clicking Upload File!");
      return;
    }

    setUploading(true);
    setUploadError(null);

    const formData = new FormData();
    formData.append("file", uploadedFile);

    axios
      .post("/api/update_db", formData)
      .then((response) => {
        alert("File Uploaded. Changes made to Dashboard!");
        console.log("Data uploaded successfully:", response.data);
      })
      .catch((error) => {
        console.error("Error uploading data:", error);
        setUploadError("Error uploading data. Please try again later.");
      })
      .finally(() => {
        setUploading(false);
      });
  };

  const styles = {
    container: {
      marginBottom: "20px",
    },
    header: {
      marginBottom: "10px",
    },
    input: {
      display: "block", // Display the input on a new line
      border: "1px solid #ccc",
      borderRadius: "4px",
      padding: "8px",
      marginBottom: "10px",
    },
    button: {
      backgroundColor: "black", // Black color
      color: "white",
      padding: "10px 20px",
      border: "none",
      borderRadius: "4px",
      cursor: "pointer",
      fontSize: "16px",
      textDecoration: "none",
      transition: "background-color 0.3s",
    },
  };

  return (
    <Container maxWidth="sm" style={styles.container}>
      <Typography variant="h2">Welcome to Admin Centre</Typography>
      <Typography variant="h3" style={styles.header}>
        Latest LWE Bill Excel File
      </Typography>
      <Typography variant="subtitle1" style={styles.header}>
        Wait 30s after upload for changes to be seen in Dashboard
      </Typography>

      <input
        type="file"
        accept=".xls,.xlsx"
        onChange={handleFileUpload}
        style={styles.input}
      />
      {uploadError && <Alert severity="error">{uploadError}</Alert>}
      <Button
        variant="contained"
        color="primary"
        style={styles.button}
        onClick={handleDataUpload}
        disabled={uploading}
      >
        {uploading ? "Uploading..." : "Upload File"}
      </Button>
      {/* Add your admin dashboard content here */}
    </Container>
  );
};

export default Admin;
