import React, { useState, useEffect, useRef } from 'react';
import { Line, Bar, Doughnut } from 'react-chartjs-2';
import { Container, Typography, Grid, Paper, Select, MenuItem } from '@mui/material';

const Dashboard = () => {
  // Dummy data for graphs
  const [selectedQuantity, setSelectedQuantity] = useState('Elec Charge');
  const [containerHeight, setContainerHeight] = useState(0);
  const containerRef = useRef(null);

  useEffect(() => {
    // Calculate the maximum height of the graphs
    const maxHeight = Math.max(
      containerRef.current.querySelector('#lineChart').offsetHeight,
      containerRef.current.querySelector('#barChart').offsetHeight,
      containerRef.current.querySelector('#doughnutChart').offsetHeight
    );
    setContainerHeight(maxHeight);
  }, []);

  const lineChartData = {
    labels: ['January', 'February', 'March', 'April', 'May', 'June', 'July'],
    datasets: [
      {
        label: 'Total Electricity Bill (in Rupees)',
        data: [10000, 15000, 20000, 18000, 22000, 25000, 30000],
        fill: false,
        borderColor: 'rgb(75, 192, 192)',
        tension: 0.1,
      },
    ],
  };

  const barChartData = {
    labels: ['Quarter 1', 'Quarter 2', 'Quarter 3', 'Quarter 4'],
    datasets: [
      {
        label: 'Frequency',
        data: [20, 30, 25, 35],
        backgroundColor: 'rgb(255, 99, 132)',
      },
    ],
  };

  const doughnutChartData = {
    labels: ['Elec Charge', 'Fixed Charge', 'Meter Rent', 'Energy Charge'],
    datasets: [
      {
        label: 'Percentage Split',
        data: [25, 20, 15, 40],
        backgroundColor: [
          'rgba(255, 99, 132, 0.6)',
          'rgba(54, 162, 235, 0.6)',
          'rgba(255, 205, 86, 0.6)',
          'rgba(75, 192, 192, 0.6)',
        ],
      },
    ],
  };

  return (
    <Container ref={containerRef}>
      <Typography variant="h4" gutterBottom>Data Dashboard</Typography>
      <Grid container spacing={3}>
        <Grid item xs={12} sm={4}>
          <Paper sx={{ p: 2, height: containerHeight }}>
            <Typography variant="h6">Graph section 1: Month on month trend of total electricity bill</Typography>
            <div id="lineChart" style={{ height: '100%' }}>
              <Line data={lineChartData} />
            </div>
          </Paper>
        </Grid>
        <Grid item xs={12} sm={4}>
          <Paper sx={{ p: 2, height: containerHeight }}>
            <Typography variant="h6">Graph section 2: Frequency distribution of avg monthly bill of quarters</Typography>
            <div id="barChart" style={{ height: '100%' }}>
              <Bar data={barChartData} />
            </div>
          </Paper>
        </Grid>
        <Grid item xs={12} sm={4}>
          <Paper sx={{ p: 2, height: containerHeight }}>
            <Typography variant="h6">Graph section 3: Month on month trend of %split of 
              <Select
                value={selectedQuantity}
                onChange={(e) => setSelectedQuantity(e.target.value)}
                sx={{ marginLeft: '10px' }}
              >
                <MenuItem value="Elec Charge">Elec Charge</MenuItem>
                <MenuItem value="Fixed Charge">Fixed Charge</MenuItem>
                <MenuItem value="Meter Rent">Meter Rent</MenuItem>
                <MenuItem value="Energy Charge">Energy Charge</MenuItem>
              </Select>
            </Typography>
            <div id="doughnutChart" style={{ height: '100%' }}>
              <Doughnut data={doughnutChartData} />
            </div>
          </Paper>
        </Grid>
      </Grid>
      <Grid container spacing={3}>
        <Grid item xs={12} sm={6}>
          <Paper sx={{ p: 2 }}>
            <Typography variant="h6">Metric Section 1: Average monthly bill of quarter ___</Typography>
            {/* Add dropdown for selecting quarter */}
          </Paper>
        </Grid>
        <Grid item xs={12} sm={6}>
          <Paper sx={{ p: 2 }}>
            <Typography variant="h6">Metric Section 2: Top 5% average bill quarters</Typography>
            {/* Add list of top 5% average bill quarters */}
          </Paper>
        </Grid>
      </Grid>
    </Container>
  );
};

export default Dashboard;
