// Login.js

import React from 'react';

const Login = ({ onLogin, adminUsers, history }) => {
  // Function to handle successful login
  const handleLogin = (userEmail) => {
    onLogin(userEmail);
    if (adminUsers.includes(userEmail)) {
      history.push('/admin');
    } else {
      history.push('/dashboard');
    }
  };

  return (
    <div>
      <h2>Login Page</h2>
      <button onClick={() => handleLogin('admin1@example.com')}>
        Login as Admin
      </button>
      <button onClick={() => handleLogin('user@example.com')}>
        Login as User
      </button>
    </div>
  );
};

export default Login;
