// App.js

import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Home from './components/Home';
import Login from './components/Login';
import Admin from './components/Admin';
import Dashboard from './components/Dashboard';

const App = () => {
  // Define state to store logged-in user and admin list
  const [loggedInUser, setLoggedInUser] = useState(null);
  const adminUsers = ['admin1@example.com', 'admin2@example.com']; // Predefined admin user list

  // Function to handle user login
  const handleLogin = (userEmail, history) => {
    setLoggedInUser(userEmail);
    if (adminUsers.includes(userEmail)) {
      history.push('/admin');
    } else {
      history.push('/dashboard');
    }
  };

  // Function to handle user logout
  const handleLogout = () => {
    setLoggedInUser(null);
  };

  // ProtectedRoute component to handle authentication
  const ProtectedRoute = ({ component: Component, isAdmin, ...rest }) => {
    if (!loggedInUser) {
      return <Navigate to="/login" />;
    }

    if (rest.path === '/admin' && !isAdmin) {
      return <Navigate to="/dashboard" />;
    }

    return <Component />;
  };

  return (
    <Router>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route
          path="/login"
          element={<Login onLogin={(userEmail, history) => handleLogin(userEmail, history)} />}
        />
        <ProtectedRoute
          path="/admin"
          element={<Admin />}
          isAdmin={adminUsers.includes(loggedInUser)}
        />
        <ProtectedRoute path="/dashboard" element={<Dashboard />} />
      </Routes>
    </Router>
  );
};

export default App;
