import React from "react";
import { useNavigate } from "react-router-dom";
import { AppBar, Toolbar, Typography, Button, IconButton } from "@mui/material";
import MenuIcon from "@mui/icons-material/Menu";

const Navbar = ({ decodedToken, setDecodedToken, adminUsers }) => {
  const navigate = useNavigate(); // Get navigate function

  const handleButtonClick = (path) => {
    navigate(path); // Navigate to the specified path
  };

  const handleLogout = () => {
    localStorage.removeItem("token"); // Clear token from local storage
    setDecodedToken(false); // Set decodedToken to false
    navigate("/"); // Navigate to the home page after logout
  };

  return (
    <AppBar position="static">
      <Toolbar sx={{ justifyContent: "space-between" }}>
        <div>
          <Button color="inherit" onClick={() => handleButtonClick("/")}>
            Home
          </Button>
          {decodedToken && (
            <Button
              color="inherit"
              onClick={() => handleButtonClick("/dashboard")}
            >
              Dashboard
            </Button>
          )}
          {decodedToken && adminUsers.includes(decodedToken.email) && (
            <Button color="inherit" onClick={() => handleButtonClick("/admin")}>
              Admin Centre
            </Button>
          )}
        </div>
        {decodedToken ? (
          <div>
            <Typography variant="body1" color="inherit" sx={{ mr: 2 }}>
              Logged in as: {decodedToken.email}
            </Typography>
            <Button color="inherit" onClick={handleLogout}>
              Logout
            </Button>
          </div>
        ) : (
          <Button color="inherit" onClick={() => handleButtonClick("/login")}>
            Login
          </Button>
        )}
      </Toolbar>
    </AppBar>
  );
};

export default Navbar;
