// Admin.js
import React, { useState } from 'react';
import axios from 'axios';
import Dashboard from './Dashboard';

const Admin = () => {
  const [uploadedFile, setUploadedFile] = useState(null);

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    setUploadedFile(file);
    console.log("Uploaded file:", file);
  };

  const handleDataUpload = () => {
    if (!uploadedFile) {
      alert("Please upload a file before clicking Upload Data!");
      return;
    }
    
    const formData = new FormData();
    formData.append('file', uploadedFile);

    axios.post('/api/update_db', formData)
      .then(response => {
        alert("File Uploaded!");
        console.log("Data uploaded successfully:", response.data);
      })
      .catch(error => {
        console.error("Error uploading data:", error);
      });
  };

  const styles = {
    container: {
      marginBottom: '20px',
    },
    header: {
      marginBottom: '10px',
    },
    input: {
      display: 'block', // Display the input on a new line
      border: '1px solid #ccc',
      borderRadius: '4px',
      padding: '8px',
      marginBottom: '10px',
    },
    button: {
      backgroundColor: 'black', // Black color
      color: 'white',
      padding: '10px 20px',
      border: 'none',
      borderRadius: '4px',
      cursor: 'pointer',
      fontSize: '16px',
      textDecoration: 'none',
      transition: 'background-color 0.3s',
    },
  };

  return (
    <div style={styles.container}>
      <h2>Welcome to Admin Centre</h2>
      <h3 style={styles.header}>Wait 30s for changes to be seen in Dashboard</h3>
      <h3 style={styles.header}>Latest LWE Bill Excel File</h3>
      <input 
        type="file" 
        accept=".xls,.xlsx" 
        onChange={handleFileUpload} 
        style={styles.input} 
      />
      <button 
        style={styles.button} 
        onClick={handleDataUpload}
      >
        Upload file
      </button>
      {/* Add your admin dashboard content here */}
    </div>
  );
};

export default Admin;
