import React, { useState, useEffect } from 'react';
import { Chart as ChartJS, registerables } from 'chart.js';
import { Bar, Line } from 'react-chartjs-2';
import { Container, Typography, Grid, Paper, CircularProgress } from '@mui/material';

ChartJS.register(...registerables);

const Dashboard = () => {
  const [loading, setLoading] = useState(true);
  const [billData, setBillData] = useState([]);
  const [quarterMetricData, setQuarterMetricData] = useState([]);
  const [monthMetricData, setMonthMetricData] = useState([]);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await fetch('/api/get_data');
      const data = await response.json();

      setBillData(data.bill_data);
      setQuarterMetricData(data.quarter_metric_data);
      setMonthMetricData(data.month_metric_data);
      setLoading(false);
      console.log('Data successfully fetched: ', data)
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  // Dyamic data for graphs
  const lineChartData = {
    labels: monthMetricData.map(item => item.Month), // x-axis labels
    datasets: [
      {
        label: 'Total Charge', // dataset label
        data: monthMetricData.map(item => item.Total_Charge), // y-axis data
        borderColor: 'rgb(53, 162, 235)', // border color
        backgroundColor: 'rgba(53, 162, 235, 0.5)', // background color
      },
    ],
  };

  // Dyamic data for bar chart
  const generateHistogramData = () => {
    // Hardcoded range and bin width
    const minAvgCharge = 0;
    const maxAvgCharge = 10000;
    const binWidth = 500;
  
    // Define bins
    const bins = Array.from({ length: Math.ceil((maxAvgCharge - minAvgCharge) / binWidth) + 1 }, (_, i) => minAvgCharge + i * binWidth);
  
    // Initialize frequency array
    const frequency = Array(bins.length - 1).fill(0);
  
    // Calculate the frequency of quarters falling into each bin
    quarterMetricData.forEach(item => {
      const binIndex = Math.floor((item.Avg_Charge - minAvgCharge) / binWidth);
      if (binIndex >= 0 && binIndex < frequency.length) {
        frequency[binIndex]++;
      }
    });
  
    // Generate labels for each bin range
    const labels = bins.slice(0, -1).map((bin, index) => {
      const nextBin = bins[index + 1];
      return `${bin}-${nextBin}`;
    });
  
    // Construct histogram data
    return {
      labels,
      datasets: [
        {
          label: 'Frequency',
          data: frequency,
          backgroundColor: 'rgba(75, 192, 192, 0.6)',
        },
      ],
    };
  };

  const stackedBarChartData = {
    // ...
  };

  return (
    <Container>
      <Typography variant="h3" gutterBottom>Data Dashboard</Typography>
      {loading ? (
        <CircularProgress />
      ) : (
        <Grid container spacing={3}>
          <Grid item xs={12} md={3}>
            <Paper sx={{ p: 2 }}>
              <Typography variant="h6">Month on Month Trend of Total Charge</Typography>
              <Line data={lineChartData} />
            </Paper>
          </Grid>
          <Grid item xs={12} md={6}>
            <Paper sx={{ p: 2 }}>
              <Typography variant="h6">Quarter Distribution of Avg Charge</Typography>
              <Bar data={generateHistogramData()} />
            </Paper>
          </Grid>
        </Grid>
      )}
    </Container>
  );
};

export default Dashboard;
