import React, { useEffect } from 'react';
import Chart from 'chart.js/auto';

const Dashboard = ({ data }) => {
  useEffect(() => {
    const ctx = document.getElementById('myChart');
    if (ctx) {
      new Chart(ctx, {
        type: 'line',
        data: data,
        options: {
          scales: {
            xAxes: [{
              type: 'time',
              time: {
                unit: 'month',
              },
            }],
            yAxes: [{
              ticks: {
                beginAtZero: true,
              },
            }],
          },
        },
      });
    }
  }, [data]);

  return <canvas id="myChart" />;
};

export default Dashboard;
