// App.js
import React, { useState, useEffect} from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { jwtDecode } from "jwt-decode";

import Home from './components/Home';
import Login from './components/Login';
import Admin from './components/Admin';
import Dashboard from './components/Dashboard';

const App = () => {
  // Define state to store token, and admin list
  const [decodedToken, setDecodedToken] = useState(false)
  const adminUsers = ['f20200445@goa.bits-pilani.ac.in']; // Predefined admin user list

  const checkAuth = () => {
    const storedToken = localStorage.getItem('token');
    if (storedToken) {
      setDecodedToken(false)
    }
    else {
      setDecodedToken(jwtDecode(storedToken))
    }
  }

  useEffect(()=> {
    checkAuth();
  },[])

  return (
    <div className="App">
      <Router>
        <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/login" element={<Login />} />

            {decodedToken ? (
              <Route path="/dashboard" element={<Dashboard />} />
            ):(
              <Route path="/dashboard" element={<Login setDecodedToken={setDecodedToken}/>} />
            )}

            {(decodedToken && adminUsers.includes(decodedToken.email)) ? (
              <Route path="/admin" element={<Admin />} />
            ):(
              <Route path="/admin" element={<Login setDecodedToken={setDecodedToken}/>} />
            )}
        </Routes>
      </Router>
    </div>
  );
};

export default App;
