import React from "react";
import { useNavigate } from "react-router-dom";

const Navbar = ({ decodedToken, setDecodedToken, adminUsers }) => {
  const navigate = useNavigate(); // Get navigate function

  const handleButtonClick = (path) => {
    navigate(path); // Navigate to the specified path
  };

  const handleLogout = () => {
    localStorage.removeItem("token"); // Clear token from local storage
    setDecodedToken(false); // Set decodedToken to false
    navigate("/"); // Navigate to the home page after logout
  };

  const navbarStyle = {
    display: "flex",
    justifyContent: "space-between", // Adjusted to push items to the ends
    alignItems: "center",
    backgroundColor: "#333",
    padding: "10px 20px",
  };

  const buttonStyle = {
    backgroundColor: "transparent",
    border: "none",
    color: "white",
    fontSize: "16px",
    cursor: "pointer",
    marginLeft: "10px",
  };

  const logoutButtonStyle = {
    ...buttonStyle,
    backgroundColor: "#f44336", // Red background color
  };

  return (
    <div style={navbarStyle}>
      <div>
        <button style={buttonStyle} onClick={() => handleButtonClick("/")}>
          Home
        </button>

        {decodedToken && (
          <button
            style={buttonStyle}
            onClick={() => handleButtonClick("/dashboard")}
          >
            Dashboard
          </button>
        )}

        {decodedToken && adminUsers.includes(decodedToken.email) && (
          <button
            style={buttonStyle}
            onClick={() => handleButtonClick("/admin")}
          >
            Admin Centre
          </button>
        )}

        {!decodedToken ? (
          <button
            style={buttonStyle}
            onClick={() => handleButtonClick("/login")}
          >
            Login
          </button>
        ) : (
          <div style={{ display: "flex", alignItems: "center" }}>
            <p style={{ color: "white", fontSize: "16px", margin: 0 }}>
              Logged in as: {decodedToken.username}
            </p>
            <button style={logoutButtonStyle} onClick={handleLogout}>
              Logout
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Navbar;
