import React from "react";
import { Typography, Container, Button } from "@mui/material";

const Home = () => {
  return (
    <Container
      sx={{
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        minHeight: "100vh",
        textAlign: "center",
        borderRadius: "20px",
        boxShadow: "0px 0px 20px rgba(0, 0, 0, 0.1)",
        padding: "2rem",
        margin: "auto", // Center the container horizontally
        maxWidth: "800px", // Limit the maximum width of the container
      }}
    >
      <Typography variant="h3" sx={{ marginBottom: "2rem" }}>
        Welcome to WattWise
      </Typography>
      <Typography variant="body1" sx={{ marginBottom: "2rem" }}>
        At WattWise, we're dedicated to helping you manage your energy
        consumption more efficiently. Our platform provides insightful data
        analytics and tools to optimize energy usage, reduce costs, and
        contribute to a more sustainable future.
      </Typography>
      <Button
        variant="contained"
        color="primary"
        href="/dashboard"
        sx={{ borderRadius: "999px" }}
      >
        Get Started
      </Button>
    </Container>
  );
};

export default Home;
