// App.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
import axios from 'axios';

import Home from './components/Home';
import Login from './components/Login';
import Admin from './components/Admin';
import Dashboard from './components/Dashboard';

const App = () => {
  // Define state to store token, admin list, and loading state
  const [decodedToken, setDecodedToken] = useState(false);
  const [adminUsers, setAdminUsers] = useState([]);
  const [loading, setLoading] = useState(true);

  const checkAuth = () => {
    const storedToken = localStorage.getItem('token');
    if (storedToken) {
      try {
        const decodedToken = jwtDecode(storedToken);
        setDecodedToken(decodedToken);
      } catch (error) {
        console.error('Error decoding token:', error);
        setDecodedToken(false);
      }
    } else {
      setDecodedToken(false);
    }
  };

  useEffect(() => {
    checkAuth();
  }, []);

  useEffect(() => {
    const fetchAdminUsers = async () => {
      try {
        const response = await axios.get('/api/get_admin');
        setAdminUsers(response.data.admins);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching admin users:', error);
        setLoading(false);
      }
    };

    fetchAdminUsers();
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="App">
      <Router>
        <Routes>
          <Route path="/" element={<Home setDecodedToken={setDecodedToken} />} />
          <Route path="/login" element={<Login setDecodedToken={setDecodedToken} />} />

          {decodedToken ? (
            <Route path="/dashboard" element={<Dashboard />} />
          ) : (
            <Route path="/dashboard" element={<Login setDecodedToken={setDecodedToken} />} />
          )}

          {decodedToken && adminUsers.includes(decodedToken.email) ? (
            <Route path="/admin" element={<Admin />} />
          ) : (
            <Route path="/admin" element={<Login setDecodedToken={setDecodedToken} />} />
          )}
        </Routes>
      </Router>
    </div>
  );
};

export default App;
