import React from "react";
import { useNavigate } from "react-router-dom";

const Navbar = ({ setDecodedToken }) => {
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem("token"); // Clear token from local storage
    setDecodedToken(false); // Set decodedToken to false
    navigate("/login"); // Redirect to login page after logout
  };

  const handleRedirect = (path) => {
    navigate(path);
  };

  return (
    <nav>
      <ul>
        <li>
          <button style={buttonStyle} onClick={() => handleRedirect("/login")}>
            Login
          </button>
        </li>
        <li>
          <button
            style={buttonStyle}
            onClick={() => handleRedirect("/dashboard")}
          >
            Dashboard
          </button>
        </li>
        <li>
          <button style={buttonStyle} onClick={() => handleRedirect("/admin")}>
            Admin Centre
          </button>
        </li>
        <li>
          <button style={logoutButtonStyle} onClick={handleLogout}>
            Logout
          </button>
        </li>
        <li>
          <button style={logoutButtonStyle} onClick={handleLogout}>
            Logout
          </button>
        </li>
      </ul>
    </nav>
  );
};

const buttonStyle = {
  backgroundColor: "black",
  color: "white",
  padding: "10px 20px",
  border: "none",
  borderRadius: "4px",
  cursor: "pointer",
  fontSize: "16px",
  margin: "5px",
  textDecoration: "none",
  transition: "background-color 0.3s",
};

const logoutButtonStyle = {
  backgroundColor: "#f44336", // Red background color
  color: "white",
  padding: "10px 20px",
  border: "none",
  borderRadius: "4px",
  cursor: "pointer",
  fontSize: "16px",
  margin: "5px",
  textDecoration: "none",
  transition: "background-color 0.3s",
};

export default Navbar;
