import React, { useState } from 'react';
import {BrowserRouter as Router, Route, Redirect} from 'react-router-dom'

import Home from './components/Home'
import Login from './components/Login'
import Admin from './components/Admin'
import Dashboard from './components/Dashboard'

const App = () => {
  // Define state being loggedInUser and adminList
  const [loggedInUser, setLoggedInUser] = useState(null)
  const adminUsers = ['f20200445@goa.bits-pilani.ac.in','shyampoduval1@gmail.com']

  // Functions that modify state (logged in user)
  const handleLogin = (userEmail) => {
    setLoggedInUser(userEmail);
  }
  const handleLogout = () => {
    setLoggedInUser(null);
  }

  // Protected Route component to handle authentication
  const ProtectedRoute = ({component: Component, isAdmin, ...rest }) => (
    <Route
    {...rest}
    render = {(props) => 
      loggedInUser && (isAdmin || rest.path !== '/admin') ? (
        <Component {...props} />
      ) : (
        <Redirect to='/login' />
      )
      }
    />
  );

  return (
    <Router>
      <div>
        <Route exact path="/" component="Home"/>
        <Route path="/login" render={(props)=>(
          <Login {...props} onLogin={handleLogin} adminUsers={adminUsers} />
        )}/>
        <ProtectedRoute path="/admin" component={Admin} isAdmin={adminUsers.includes(loggedInUser)} />
        <ProtectedRoute path="/dashboard" component={Dashboard}/>
      </div>
    </Router>
  );
};

export default App;
