// App.js

import React, { useState } from 'react';
import { BrowserRouter as Router, Route } from 'react-router-dom';
import Home from './components/Home';
import Login from './components/Login';
import Admin from './components/Admin';
import Dashboard from './components/Dashboard';

const App = () => {
  // Define state to store logged-in user and admin list
  const [loggedInUser, setLoggedInUser] = useState(null);
  const adminUsers = ['admin1@example.com', 'admin2@example.com']; // Predefined admin user list

  // Function to handle user login
  const handleLogin = (userEmail, history) => {
    setLoggedInUser(userEmail);
    if (adminUsers.includes(userEmail)) {
      history.push('/admin');
    } else {
      history.push('/dashboard');
    }
  };

  // Function to handle user logout
  const handleLogout = () => {
    setLoggedInUser(null);
  };

  // ProtectedRoute component to handle authentication
  const ProtectedRoute = ({ component: Component, isAdmin, ...rest }) => (
    <Route
      {...rest}
      render={(props) =>
        loggedInUser && (isAdmin || rest.path !== '/admin') ? (
          <Component {...props} />
        ) : (
          <Login {...props} onLogin={(userEmail) => handleLogin(userEmail, props.history)} />
        )
      }
    />
  );

  return (
    <Router>
      <div>
        <Route exact path="/" component={Home} />
        <ProtectedRoute path="/admin" component={Admin} isAdmin={adminUsers.includes(loggedInUser)} />
        <ProtectedRoute path="/dashboard" component={Dashboard} />
      </div>
    </Router>
  );
};

export default App;
